import React, { useState, useEffect } from "react";
import "./tweet.css";
// import axios from "axios";
function Twitter() {
    const [data, setData] = useState([]);
    const [searchdata,setSearchdata] = usestate("");
    const [searchResults, setSearchResults] = useState([]);

    useEffect(() => {
        fetch("http://localhost:3001/api/tweet")
            .then(response => response.json())
            .then(data => setData(data));
    }, []);
    useEffect(() => {
        setSearchResults(
            data.filter(tweet =>
                tweet.name.toLowerCase().includes(searchdata.toLowerCase())
                )
        );
    },[searchdata,data]);

    const handlesearch = event =>{
        setSearchdata(event.target.value)
    };
    const handlesubmit = event =>{
        event.preventDefault();
    }
    return (
        <div>
            <div className="search">
                <input className="input" type="text"  value={searchdata} onchange placeholder="Search.." />
                &nbsp;
                <button type="button" className="button" onclick={(e)=>handlesubmit}>Search</button>
            </div>
            <div>
                {data.map(tweet => (
                    <div className="grid_tweet ">
                        <div className="box" key="tweet.id">
                            <strong>URL: </strong>{tweet.url}<br />
                            <strong>Tweet_link: </strong>{tweet.tweet_link}<br />
                            <strong>User_id: </strong>{tweet.user_id}<br />
                            <strong>Hastag: </strong>{tweet.hastag}<br />
                            <strong>Retweet_count: </strong>{tweet.retweet_count}<br />
                            <strong> Share_count: </strong>{tweet.share_count}<br />
                            <strong>Description: </strong>{tweet.description}<br />
                            <strong>Following: </strong>{tweet.following}<br />
                            <strong>Followers: </strong>{tweet.followers}
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Twitter;
