import React, { useState, useEffect } from "react";
import "./data.css";

function Twitter() {
  const [data, setData] = useState([]);
  const [search, setSearch] = useState('');
  const [expandedIndex, setExpandedIndex] = useState(-1);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(`http://localhost:3001/api/twet?search=${search}`);
        const data = await response.json();
        setData(data);
      } catch (error) {
        console.error(error);
      }
    };
    fetchData();
  }, [search]);

  const handleSearch = event => {
    setSearch(event.target.value);
    setExpandedIndex(-1); // reset expanded box when search changes
  };

  const handleBoxClick = index => {
    setExpandedIndex(index === expandedIndex ? -1 : index); // toggle expanded state
  };

  return (
    <div>
      <div className="search">
        <input
          className="input"
          type="text"
          placeholder="search...."
          value={search}
          onChange ={(event)=>handleSearch(event)}/>
          &nbsp;
      </div>
      <div>
        {data.map((item, index) => (
          <div className="grid_item" key={item.id}>
            <div className={"box " + (index === expandedIndex ? "expanded" : "")} onClick={() => handleBoxClick(index)}>
              <strong>URL: </strong>{item.url}<br />
              <strong>Tweet_link: </strong>{item.tweet_link}<br />
              <strong>User_id: </strong>{item.user_id}<br />
              <strong>Hastag: </strong>{item.hastag}<br />
              <strong>Retweet_count: </strong>{item.retweet_count}<br />
              <strong> Share_count: </strong>{item.share_count}<br />
              <strong>Description: </strong>{item.description}<br />
              <strong>Following: </strong>{item.following}<br />
              <strong>Followers: </strong>{item.followers}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Twitter;
