import React, { useState, useEffect } from "react";
import "./data.css";
function Twitter() {
  const [data, setData] = useState([]);
  const [search, setSearch] = useState('');
  const [filteredsearch, setFilteredsearch] = useState([]);

  // useEffect(() => {
  //   fetch("http://localhost:3001/api/tweet")
  //     .then(response => response.json())
  //     .then(data => setData(data));
  // }, []);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(`http://localhost:3001/api/tweet?${search}`);
        const data = await response.json();
        setData(data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchData();
  }, [search]);


  useEffect(() => {
    setFilteredsearch(
      data.filter(item =>
        Object.values(item).join('').toLowerCase().includes(search.toLowerCase())
      )
    );
  }, [search, data]);

  const handleSearch = event => {
    setSearch(event.target.value);
  };


  return (
    <div>
      <div className="search">
        <input
          className="input"
          type="text"
          placeholder="search...."
          value={search}
          onChange={handleSearch}/>
      </div>
      <div>
        {filteredsearch.map(item => (
          <div className="grid_item ">
            <div className="box" key="item.id">
              <strong>URL: </strong>{item.url}<br />
              <strong>Tweet_link: </strong>{item.tweet_link}<br />
              <strong>User_id: </strong>{item.user_id}<br />
              <strong>Hastag: </strong>{item.hastag}<br />
              <strong>Retweet_count: </strong>{item.retweet_count}<br />
              <strong> Share_count: </strong>{item.share_count}<br />
              <strong>Description: </strong>{item.description}<br />
              <strong>Following: </strong>{item.following}<br />
              <strong>Followers: </strong>{item.followers}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Twitter;
